package chapter05;

public class CharLiterals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a = '가';
		char b = '나';
		char c = '\u0041';	// 유니코드 문자표 A
		
		boolean d = true; 	// 논리형 자료형
		
		System.out.println("실행 결과 : " + a + " " + b + " " + c + " " + d);
		
		return;
	}

}
